import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-fetch-movie-data',
  templateUrl: './fetch-movie-data.component.html',
})
export class FetchMovieDataComponent {
  public movies: Movie[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<Movie[]>(baseUrl + '/movies').subscribe(movies => {
      this.movies = movies;
    });
  }
}

interface Movie {
  id: number;
  imdbId: string;
  popularity: number;
  budget: number;
  revenue: number;
  originalTitle: string;
  cast: string[];
  homepage: string;
  director: string;
  tagline: string;
  keywords: string[];
  overview: string;
  runtime: number;
  genres: string[];
  productionCompanies: string[];
  releaseDate: string;
  voteCount: number;
  voteAverage: number;
  releaseYear: number;
  budgetAdjusted: number;
  revenueAdjusted: number;
}
